#' Multiple Response Crosstabs
#'
#' 
#' @param data .
#' @param resps .
#' @param group .
#' @param endorsed .
#' @param order .
#' @param computedValues .
#' @param xaxis .
#' @param bartype .
#' @param size .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$crosstab} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$crosstab$asDF}
#'
#' \code{as.data.frame(results$crosstab)}
#'
#' @export
crosstabs <- function(
    data,
    resps,
    group,
    endorsed = 1,
    order = "decreasing",
    computedValues = "count",
    xaxis = "xrows",
    bartype = "dodge",
    size = "medium") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("crosstabs requires jmvcore to be installed (restart may be required)")

    if ( ! missing(resps)) resps <- jmvcore::resolveQuo(jmvcore::enquo(resps))
    if ( ! missing(group)) group <- jmvcore::resolveQuo(jmvcore::enquo(group))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(resps), resps, NULL),
            `if`( ! missing(group), group, NULL))

    for (v in resps) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- crosstabsOptions$new(
        resps = resps,
        group = group,
        endorsed = endorsed,
        order = order,
        computedValues = computedValues,
        xaxis = xaxis,
        bartype = bartype,
        size = size)

    analysis <- crosstabsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

